import React, { useState } from 'react';
import { PageContainer } from '@ant-design/pro-layout';
import { Card, Button } from 'antd';
import { marked } from 'marked';
import hljs from 'highlight.js';
import styles from './index.less';
import 'highlight.js/styles/atom-one-dark.css';
import type { IRouteComponentProps } from 'umi';

const text = window.atob(
  '',
);

marked.setOptions({
  renderer: new marked.Renderer(),
  highlight: function (code, lang) {
    const language = hljs.getLanguage(lang) ? lang : 'plaintext';
    return hljs.highlight(code, { language }).value;
  },
  langPrefix: 'hljs language-', // highlight.js css expects a top-level 'hljs' class.
  pedantic: false,
  gfm: true,
  breaks: false,
  sanitize: true,
  smartLists: true,
  smartypants: false,
  xhtml: false,
});

const NewArticle: React.FC<IRouteComponentProps<{ id: string }, {}>> = (props) => {
  // IRouteComponentProps泛型参数的第一个是params的参数，第二个是query的参数
  const [markdownText, setMarkdownText] = useState(props.match.params.id ? text : '');

  return (
    <PageContainer
      header={{
        title: props.match.params.id ? '编辑文章' : '新建文章',
      }}
      footer={[
        <Button key="2" type="primary" onClick={() => {}}>
          提交
        </Button>,
      ]}
    >
      <Card>
        <div className={styles.wrap}>
          <div className={styles.textWrap}>
            <h3 className={styles.textTitle}>input markdown text here:</h3>
            <textarea
              value={markdownText}
              className={styles.textarea}
              onChange={(e) => {
                setMarkdownText(e.target.value);
              }}
            />
          </div>
          <div className={styles.previewWrap}>
            <h3 className={styles.textTitle}>this is markdown preview:</h3>
            <div
              className={styles.preview}
              dangerouslySetInnerHTML={{ __html: marked.parse(markdownText) }}
            ></div>
          </div>
        </div>
      </Card>
    </PageContainer>
  );
};

export default NewArticle;
